    @Test
    public void getBinFromText() {
        String input = " !\"#$%&'()*+,-./0123456789:;<=>?@ABCDEFGHIJKLMNOPQRSTUVWXYZ[\\]^_`abcdefghijklmnopqrstuvwxyz{|}~";

        String expected = "";
        String actual = Utils.getBinFromText(input);

        assertEquals(expected, actual);
    }

